from buildbot.process.buildstep import BuildStep
from buildbot.status import progress
from buildbot.status.results import SUCCESS, WARNINGS, FAILURE, SKIPPED, \
     EXCEPTION, RETRY, worst_status
from twisted.python import log
from autobuilder.config import *

class GetDistroVersion(BuildStep):
    haltOnFailure = False 
    flunkOnFailure = True 
    name = "GetDistroVersion" 
    def __init__(self, factory, argdict=None, **kwargs):
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.description = "Get Distro Version"
        self.slaveworkdir=os.path.join(os.path.join(YOCTO_ABBASE, "yocto-slave"))
        # Timeout needs to be passed to LoggingBuildStep as a kwarg
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.timeout = 100000
        kwargs['timeout']=self.timeout
        self._pendingLogObservers = []
        self._acquiringLock = None
        self.stopped = False

    def start(self):
        import os
        buildername=self.getProperty("buildername")
        if "poky" in self.distro:
            pokyconf=self.slaveworkdir + "/" + buildername + "/build/meta-yocto/conf/distro/poky.conf"
            distrofile = open(pokyconf, "r")
            for line in distrofile:
                if 'DISTRO_VERSION =' in line:
                    distroversion=line.replace("DISTRO_VERSION = ", "").replace("-${DATE}","").replace('"','')
            self.setProperty('distroversion', distroversion)
        self.finished(SUCCESS)


