from buildbot.steps.shell import ShellCommand
from buildbot.status import progress
from buildbot.status.results import SUCCESS, FAILURE, WARNINGS
from buildbot.process.properties import WithProperties
from twisted.python import log
from autobuilder.config import *
import re

class GetDistroVersion(ShellCommand):
    haltOnFailure = False 
    flunkOnFailure = True 
    name = "GetDistroVersion"
    def __init__(self, factory, argdict=None, **kwargs):
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.description = "Get Distro Version"
        self.distroversion = None
        self.slaveworkdir=os.path.join(os.path.join(YOCTO_ABBASE, "yocto-slave"))
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        ShellCommand.__init__(self, **kwargs)
        self.property_changes = {}

    def start(self):
        import os
        buildername=self.getProperty("buildername")
        pokyconf=self.slaveworkdir + "/" + buildername + "/build/meta-yocto/conf/distro/poky.conf|grep 'DISTRO_VERSION = \"'"
        cmd = 'cat ' + pokyconf
        self.command = cmd
        ShellCommand.start(self)

    def commandComplete(self, cmd):
        if cmd.didFail():
            return
        result = cmd.logs['stdio'].getText()
        distroversion = result.replace("DISTRO_VERSION = ", "").replace("-${DATE}","").replace('"','').strip()
        self.setProperty('distroversion', distroversion, "Setting Distro Version")

    def getText(self, cmd, results):
        return ShellCommand.getText(self, cmd, results)
